// JobsList.js (assumed based on context)
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../index.css';

const JobsList = () => {
  const [jobs, setJobs] = useState({});
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/api/user/jobs/available', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setJobs(response.data);
      } catch (err) {
        setMessage(err.response?.data?.message || 'Error fetching jobs');
      }
    };
    fetchJobs();
  }, []);

  const handleApply = async (jobId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `/api/jobs/${jobId}/apply`, // This might be the wrong endpoint!
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(response.data.message);
      setJobs(response.data.availableJobs);
      // Optionally, navigate to applications page or refresh
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error applying for job');
    }
  };

  return (
    <div className="jobs-container">
      <h2 className="jobs-title">Available Jobs</h2>
      {message && <p className="message">{message}</p>}
      {Object.keys(jobs).length > 0 ? (
        Object.keys(jobs).map((category) => (
          <div key={category} className="category-section">
            <h3 className="category-title">{category}</h3>
            <div className="jobs-list">
              {jobs[category].map((job) => (
                <div key={job._id} className="job-card">
                  <h4 className="job-title">{job.title}</h4>
                  <p className="job-detail"><strong>Description:</strong> {job.description}</p>
                  <p className="job-detail"><strong>Category:</strong> {job.category}</p>
                  <p className="job-detail"><strong>Location:</strong> {job.location}</p>
                  <p className="job-detail"><strong>Budget:</strong> ${job.budget}</p>
                  <button className="apply-btn" onClick={() => handleApply(job._id)}>
                    Apply
                  </button>
                </div>
              ))}
            </div>
          </div>
        ))
      ) : (
        <p className="no-jobs">No jobs available at the moment.</p>
      )}
    </div>
  );
};

export default JobsList;
