import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../index.css';

const Applications = () => {
  const [applications, setApplications] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('token');
        if (!token) {
          setMessage('No authentication token found. Please log in.');
          return;
        }
        console.log('Fetching applications with token:', token);
        const response = await axios.get('/api/user/jobs/applications', {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log('Applications API Response Data:', response.data);
        setApplications(response.data || []);
      } catch (err) {
        console.error('Error fetching applications - Full Error:', err);
        console.error('Error Response:', err.response);
        setMessage(err.response?.data?.message || 'Error fetching applications');
      } finally {
        setLoading(false);
      }
    };
    fetchApplications();
  }, []);

  return (
    <div className="applications-container">
      <h2 className="applications-title">Your Applications</h2>
      {message && (
        <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </p>
      )}
      {loading ? (
        <p className="loading">Loading applications...</p>
      ) : (
        <div className="applications-list">
          {applications.length > 0 ? (
            applications.map((app) => (
              <div key={app._id} className="application-card">
                <h3 className="application-job-title">{app.job?.title || 'N/A'}</h3>
                <p className="application-detail"><strong>Description:</strong> {app.job?.description || 'N/A'}</p>
                <p className="application-detail"><strong>Category:</strong> {app.job?.category || 'N/A'}</p>
                <p className="application-detail"><strong>Budget:</strong> ${app.job?.budget || 0}</p>
                <p className="application-detail"><strong>Location:</strong> {app.job?.location || 'N/A'}</p>
                <p className="application-detail"><strong>Status:</strong> {app.status || 'pending'}</p>
                <p className="application-detail"><strong>Applied On:</strong> {app.appliedAt ? new Date(app.appliedAt).toLocaleDateString() : 'N/A'}</p>
              </div>
            ))
          ) : (
            <p className="no-applications">No applications yet.</p>
          )}
        </div>
      )}
      <button className="back-btn" onClick={() => navigate('/dashboard')}>
        Back to Dashboard
      </button>
      <button className="jobs-btn" onClick={() => navigate('/jobs')}>
        Browse More Jobs
      </button>
    </div>
  );
};

export default Applications;
