import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../index.css';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');
  const [selectedSection, setSelectedSection] = useState('basic');
  const [editSection, setEditSection] = useState(null);
  const [editData, setEditData] = useState({});
  const [profilePhoto, setProfilePhoto] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const token = localStorage.getItem('token');
      const role = localStorage.getItem('role');
      if (!token || !role) {
        setError('Not authenticated. Please log in to access the dashboard.');
        return;
      }

      try {
        const response = await axios.get(
          role === 'worker'
            ? `${process.env.REACT_APP_API_URL}/api/user/worker/me`
            : `${process.env.REACT_APP_API_URL}/api/user/professional-body/me`,
          {
            headers: { Authorization: `Bearer ${token}` }
          }
        );
        setUser(response.data);
        setEditData(response.data);
        setProfilePhoto(response.data.profilePhoto || null);
      } catch (err) {
        setError(err.response?.data?.message || 'Error fetching user data. Please try logging in again.');
      }
    };

    fetchUserData();
  }, []);

  const handleSectionSelect = (section) => {
    setSelectedSection(section);
    setEditSection(null);
  };

  const handleEdit = (section) => {
    setEditSection(section);
    setEditData({ ...user });
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const handleSave = async (section) => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    const endpoint = role === 'worker' ? 'worker' : 'professional-body';

    const allowedFields = {
      basic: ['name', 'email', 'contactNumber'],
      worker: ['category', 'skills', 'experience', 'availability'],
      professional: ['type', 'location', 'description']
    };

    const updates = {};
    allowedFields[section].forEach((field) => {
      if (editData[field] !== undefined) {
        if (field === 'skills') {
          updates[field] = Array.isArray(editData[field])
            ? editData[field]
            : editData[field].split(',').map(skill => skill.trim());
        } else if (field === 'experience') {
          updates[field] = Number(editData[field]);
        } else {
          updates[field] = editData[field];
        }
      }
    });

    try {
      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/user/${endpoint}/me`,
        updates,
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      setUser(response.data);
      setEditData(response.data);
      setEditSection(null);
      setError('');
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Error updating user data.';
      const errorDetails = err.response?.data?.details || '';
      setError(`${errorMsg}${errorDetails ? `: ${errorDetails}` : ''}`);
    }
  };

  const handleCancel = () => {
    setEditSection(null);
    setEditData({ ...user });
  };

  const handlePhotoUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) {
      setError('No file selected for upload.');
      return;
    }

    const maxSize = 5 * 1024 * 1024;
    if (file.size > maxSize) {
      setError('File size exceeds 5MB limit.');
      return;
    }

    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png'];
    if (!allowedTypes.includes(file.type)) {
      setError('Only JPEG, JPG, or PNG images are allowed.');
      return;
    }

    const formData = new FormData();
    formData.append('profilePhoto', file);

    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    const endpoint = role === 'worker' ? 'worker' : 'professional-body';

    try {
      const response = await axios.put(
        `${process.env.REACT_APP_API_URL}/api/user/${endpoint}/photo`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      setProfilePhoto(response.data.profilePhoto);
      setUser({ ...user, profilePhoto: response.data.profilePhoto });
      setError('');
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Error uploading profile photo.';
      const errorDetails = err.response?.data?.details || '';
      setError(`${errorMsg}${errorDetails ? `: ${errorDetails}` : ''}`);
    }
  };

  if (error) {
    return (
      <div className="dashboard-container">
        <div className="dashboard-content">
          <div className="message error">{error}</div>
          <button className="logout-btn" onClick={() => navigate('/login')}>
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return <div className="dashboard-container">Loading...</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-sidebar">
        <div
          className={`detail-header ${selectedSection === 'basic' ? 'active' : ''}`}
          onClick={() => handleSectionSelect('basic')}
        >
          Basic Information
        </div>
        {localStorage.getItem('role') === 'worker' && (
          <div
            className={`detail-header ${selectedSection === 'worker' ? 'active' : ''}`}
            onClick={() => handleSectionSelect('worker')}
          >
            Worker Details
          </div>
        )}
        {localStorage.getItem('role') === 'worker' && (
          <div
            className={`detail-header ${selectedSection === 'jobs' ? 'active' : ''}`}
            onClick={() => navigate('/jobs')}
          >
            Browse Jobs
          </div>
        )}
        {localStorage.getItem('role') === 'professional-body' && (
          <div
            className={`detail-header ${selectedSection === 'professional' ? 'active' : ''}`}
            onClick={() => handleSectionSelect('professional')}
          >
            Professional Body Details
          </div>
        )}
        <div
          className="detail-header logout-btn"
          onClick={() => {
            localStorage.removeItem('token');
            localStorage.removeItem('role');
            navigate('/login');
          }}
        >
          Logout
        </div>
      </div>
      <div className="dashboard-content">
        <div className="profile-photo-container">
          <img
            src={profilePhoto || 'https://via.placeholder.com/120?text=Profile'}
            alt="Profile"
            className="profile-photo"
          />
          <label className="upload-photo-btn">
            <span>📷</span>
            <input
              type="file"
              accept="image/*"
              onChange={handlePhotoUpload}
            />
          </label>
        </div>
        <h2 className="dashboard-title">Welcome, {user.name || 'User'}!</h2>
        <div className="user-details">
          {selectedSection === 'basic' && (
            <div className="detail-content">
              {editSection === 'basic' ? (
                <>
                  <div className="detail-item">
                    <label>Name:</label>
                    <input
                      type="text"
                      name="name"
                      value={editData.name || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="detail-item">
                    <label>Email:</label>
                    <input
                      type="email"
                      name="email"
                      value={editData.email || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="detail-item">
                    <label>Contact Number:</label>
                    <input
                      type="text"
                      name="contactNumber"
                      value={editData.contactNumber || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="actions">
                    <button className="save-btn" onClick={() => handleSave('basic')}>
                      Save
                    </button>
                    <button className="cancel-btn" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <p className="detail-item">
                    <label>Name:</label> {user.name || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Email:</label> {user.email || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Contact Number:</label> {user.contactNumber || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Role:</label> {localStorage.getItem('role') || 'N/A'}
                  </p>
                  <div className="actions">
                    <button className="edit-btn" onClick={() => handleEdit('basic')}>
                      Edit
                    </button>
                  </div>
                </>
              )}
            </div>
          )}

          {selectedSection === 'worker' && localStorage.getItem('role') === 'worker' && (
            <div className="detail-content">
              {editSection === 'worker' ? (
                <>
                  <div className="detail-item">
                    <label>Category:</label>
                    <input
                      type="text"
                      name="category"
                      value={editData.category || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="detail-item">
                    <label>Skills:</label>
                    <input
                      type="text"
                      name="skills"
                      value={editData.skills?.join(', ') || ''}
                      onChange={(e) =>
                        setEditData({ ...editData, skills: e.target.value.split(',').map(skill => skill.trim()) })
                      }
                    />
                  </div>
                  <div className="detail-item">
                    <label>Experience:</label>
                    <input
                      type="number"
                      name="experience"
                      value={editData.experience || 0}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="detail-item">
                    <label>Availability:</label>
                    <input
                      type="text"
                      name="availability"
                      value={editData.availability || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="actions">
                    <button className="save-btn" onClick={() => handleSave('worker')}>
                      Save
                    </button>
                    <button className="cancel-btn" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <p className="detail-item">
                    <label>Category:</label> {user.category || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Skills:</label> {user.skills?.join(', ') || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Experience:</label> {user.experience || '0'} years
                  </p>
                  <p className="detail-item">
                    <label>Availability:</label> {user.availability || 'N/A'}
                  </p>
                  <div className="actions">
                    <button className="edit-btn" onClick={() => handleEdit('worker')}>
                      Edit
                    </button>
                  </div>
                </>
              )}
            </div>
          )}

          {selectedSection === 'professional' && localStorage.getItem('role') === 'professional-body' && (
            <div className="detail-content">
              {editSection === 'professional' ? (
                <>
                  <div className="detail-item">
                    <label>Type:</label>
                    <input
                      type="text"
                      name="type"
                      value={editData.type || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="detail-item">
                    <label>Location:</label>
                    <input
                      type="text"
                      name="location"
                      value={editData.location || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="detail-item">
                    <label>Description:</label>
                    <textarea
                      name="description"
                      value={editData.description || ''}
                      onChange={handleEditChange}
                    />
                  </div>
                  <div className="actions">
                    <button className="save-btn" onClick={() => handleSave('professional')}>
                      Save
                    </button>
                    <button className="cancel-btn" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <p className="detail-item">
                    <label>Type:</label> {user.type || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Location:</label> {user.location || 'N/A'}
                  </p>
                  <p className="detail-item">
                    <label>Description:</label> {user.description || 'N/A'}
                  </p>
                  <div className="actions">
                    <button className="edit-btn" onClick={() => handleEdit('professional')}>
                      Edit
                    </button>
                  </div>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
