import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../index.css';

const Applications = () => {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/api/applications`,
          {
            headers: { Authorization: `Bearer ${token}` }
          }
        );

        setApplications(response.data.map(app => ({
          ...app,
          appliedDate: new Date(app.appliedAt).toLocaleDateString(),
          status: app.status.charAt(0).toUpperCase() + app.status.slice(1)
        })));
      } catch (err) {
        setMessage(err.response?.data?.message || 'Error loading applications');
      } finally {
        setLoading(false);
      }
    };

    fetchApplications();
  }, []);

  const handleStatusChange = async (applicationId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/applications/${applicationId}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setApplications(applications.map(app => 
        app._id === applicationId ? { ...app, status: newStatus } : app
      ));
    } catch (err) {
      setMessage(err.response?.data?.message || 'Status update failed');
    }
  };

  return (
    <div className="applications-container">
      <h2 className="applications-title">Applicant Details</h2>
      {message && (
        <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </p>
      )}
      {loading ? (
        <p>Loading applications...</p>
      ) : applications.length > 0 ? (
        applications.map((app) => (
          <div key={app._id} className="application-card">
            {/* Render application details */}
          </div>
        ))
      ) : (
        <p className="no-applications">No applications yet</p>
      )}
    </div>
  );
};

export default Applications;
