import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../index.css';

const Applications = () => {
  const [applications, setApplications] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchApplications = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('token');
        const role = localStorage.getItem('role');
        if (!token) {
          setMessage('No authentication token found. Please log in.');
          return;
        }
        console.log('Fetching applications with token:', token);
        const response = await axios.get('/api/user/jobs/applications', {
          headers: { Authorization: `Bearer ${token}` }
        });
        console.log('Applications API Response Data:', response.data);
        setApplications(response.data || []);
      } catch (err) {
        console.error('Error fetching applications - Full Error:', err);
        console.error('Error Response:', err.response);
        setMessage(err.response?.data?.message || 'Error fetching applications');
      } finally {
        setLoading(false);
      }
    };
    fetchApplications();
  }, []);

  const handleAccept = async (applicationId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `/api/user/jobs/applications/${applicationId}/accept`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(response.data.message);
      setApplications(applications.map(app =>
        app._id === applicationId ? { ...app, status: 'accepted' } : app
      ));
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Accept Error:', err.response ? err.response.data : err.message);
      setMessage(err.response?.data?.message || 'Error accepting application');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const handleReject = async (applicationId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `/api/user/jobs/applications/${applicationId}/reject`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMessage(response.data.message);
      setApplications(applications.map(app =>
        app._id === applicationId ? { ...app, status: 'rejected' } : app
      ));
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Reject Error:', err.response ? err.response.data : err.message);
      setMessage(err.response?.data?.message || 'Error rejecting application');
      setTimeout(() => setMessage(''), 3000);
    }
  };

  const role = localStorage.getItem('role');

  return (
    <div className="applications-container">
      <h2 className="applications-title">
        {role === 'worker' ? 'Your Applications' : 'View Applications'}
      </h2>
      {message && (
        <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </p>
      )}
      {loading ? (
        <p className="loading">Loading applications...</p>
      ) : (
        <div className="applications-list">
          {applications.length > 0 ? (
            applications.map((app) => (
              <div key={app._id} className="application-card">
                {role === 'worker' ? (
                  <>
                    <h3 className="application-job-title">{app.job?.title || 'N/A'}</h3>
                    <p className="application-detail"><strong>Description:</strong> {app.job?.description || 'N/A'}</p>
                    <p className="application-detail"><strong>Category:</strong> {app.job?.category || 'N/A'}</p>
                    <p className="application-detail"><strong>Budget:</strong> ${app.job?.budget || 0}</p>
                    <p className="application-detail"><strong>Location:</strong> {app.job?.location || 'N/A'}</p>
                    <p className="application-detail"><strong>Status:</strong> {app.status || 'pending'}</p>
                    <p className="application-detail"><strong>Applied On:</strong> {app.appliedAt ? new Date(app.appliedAt).toLocaleDateString() : 'N/A'}</p>
                  </>
                ) : (
                  <>
                    <h3 className="application-job-title">{app.job?.title || 'N/A'}</h3>
                    <p className="application-detail"><strong>Job Description:</strong> {app.job?.description || 'N/A'}</p>
                    <p className="application-detail"><strong>Job Category:</strong> {app.job?.category || 'N/A'}</p>
                    <p className="application-detail"><strong>Worker Name:</strong> {app.worker?.name || 'Unknown'}</p>
                    <p className="application-detail"><strong>Worker Email:</strong> {app.worker?.email || 'Not provided'}</p>
                    <p className="application-detail"><strong>Worker Category:</strong> {app.worker?.category || 'Not specified'}</p>
                    <p className="application-detail"><strong>Contact Number:</strong> {app.worker?.contactNumber || 'Not provided'}</p>
                    <p className="application-detail"><strong>Status:</strong> {app.status || 'pending'}</p>
                    <p className="application-detail"><strong>Applied On:</strong> {app.appliedAt ? new Date(app.appliedAt).toLocaleDateString() : 'N/A'}</p>
                    {app.status === 'pending' ? (
                      <div className="application-actions">
                        <button className="accept-btn" onClick={() => handleAccept(app._id)}>
                          Accept
                        </button>
                        <button className="reject-btn" onClick={() => handleReject(app._id)}>
                          Reject
                        </button>
                      </div>
                    ) : (
                      <p className={`status-message ${app.status}`}>
                        Application {app.status}
                      </p>
                    )}
                  </>
                )}
              </div>
            ))
          ) : (
            <p className="no-applications">No applications yet.</p>
          )}
        </div>
      )}

      {role === 'worker' && (
        <button className="jobs-btn" onClick={() => navigate('/jobsList')}>
          Browse More Jobs
        </button>
      )}
    </div>
  );
};

export default Applications;
