import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [role, setRole] = useState('worker');
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLoginChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleRegisterChange = (e) => {
    setRegisterData({ ...registerData, [e.target.name]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        `http://localhost:5001/api/auth/login/${role}`,
        loginData
      );
      localStorage.setItem('token', response.data.token);
      localStorage.setItem('role', role);
      console.log('Login Successful - Token:', response.data.token); // Debug log
      console.log('Role:', role); // Debug log
      console.log('localStorage Token:', localStorage.getItem('token')); // Debug log
      console.log('localStorage Role:', localStorage.getItem('role')); // Debug log
      navigate('/dashboard');
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Error logging in';
      setMessage(errorMsg);
      console.error('Login Error:', errorMsg); // Debug log
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        `http://localhost:5001/api/auth/register/${role}`,
        registerData
      );
      setMessage('Registration successful! Please log in.');
      console.log('Registration Successful:', response.data); // Debug log
      setRegisterData({ name: '', email: '', password: '' });
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Error registering';
      setMessage(errorMsg);
      console.error('Register Error:', errorMsg); // Debug log
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">Login to TaskConnect</h2>
        <div className="role-tabs">
          <button
            className={`tab ${role === 'worker' ? 'active' : ''}`}
            onClick={() => setRole('worker')}
          >
            Worker
          </button>
          <button
            className={`tab ${role === 'professional-body' ? 'active' : ''}`}
            onClick={() => setRole('professional-body')}
          >
            Professional Body
          </button>
        </div>

        {/* Login Form */}
        <form className="login-form" onSubmit={handleLogin}>
          <div className="input-group">
            <span className="input-icon">ðŸ“§</span>
            <input
              type="email"
              name="email"
              value={loginData.email}
              onChange={handleLoginChange}
              placeholder="Email"
              required
            />
          </div>
          <div className="input-group">
            <span className="input-icon">ðŸ”’</span>
            <input
              type="password"
              name="password"
              value={loginData.password}
              onChange={handleLoginChange}
              placeholder="Password"
              required
            />
          </div>
          <button type="submit" className="login-btn">
            Login
          </button>
        </form>

        {/* Register Form */}
        <h3 className="login-title">Register</h3>
        <form className="login-form" onSubmit={handleRegister}>
          <div className="input-group">
            <span className="input-icon">ðŸ‘¤</span>
            <input
              type="text"
              name="name"
              value={registerData.name}
              onChange={handleRegisterChange}
              placeholder="Name"
              required
            />
          </div>
          <div className="input-group">
            <span className="input-icon">ðŸ“§</span>
            <input
              type="email"
              name="email"
              value={registerData.email}
              onChange={handleRegisterChange}
              placeholder="Email"
              required
            />
          </div>
          <div className="input-group">
            <span className="input-icon">ðŸ”’</span>
            <input
              type="password"
              name="password"
              value={registerData.password}
              onChange={handleRegisterChange}
              placeholder="Password"
              required
            />
          </div>
          <button type="submit" className="login-btn">
            Register
          </button>
        </form>

        {message && (
          <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
};

export default Login;
